let interval=50,blinkInterval=600;elmList=document.getElementsByClassName("typous");for(let e=0;e<elmList.length;e++){elm=elmList[e];let n=elm.innerHTML;elm.innerHTML="|",type(n,elm)}function type(e,n){setTimeout((()=>{let t=e.charAt(0);""!=t?(n.innerHTML=n.innerHTML.slice(0,-1)+t+"|",type(e.slice(1),n)):blink(n)}),interval)}function blink(e){setTimeout((()=>{e.innerHTML=e.innerHTML.slice(0,-1),setTimeout((()=>{e.innerHTML+="|",blink(e)}),blinkInterval)}),blinkInterval)}
